<template>  <transition-group name="container">    <div        class="clipboard-container"        key="clipboard-container"        :class="{'clipboard-container-search': searchBoxState.visible}"    >      <n-infinite-scroll          v-if="clipboardItems && clipboardItems.length > 0"          :distance="9"          @load="loadMoreItems"      >        <transition-group name="item" tag="ul">          <ClipboardItem              v-for="(item, index) in clipboardItems"              :key="item.id"              :item="item"              :index="index"          />        </transition-group>        <!-- 最底部内容展示 -->        <div v-if="scrollState.isLoading" class="loading-indicator">          <n-spin :description="currentLanguage.pages.list.dataLoading"/>        </div>        <div v-if="!scrollState.hasMore" class="no-more-indicator">          {{ currentLanguage.pages.list.allLoaded }}        </div>      </n-infinite-scroll>      <!-- 无数据展示 -->      <n-empty v-else description="currentLanguage.pages.list.noClipboardRecords" class="empty"/>    </div>  </transition-group></template><script setup lang="ts">import {NEmpty, NInfiniteScroll, NSpin} from 'naive-ui';import ClipboardItem from './ClipboardItem.vue';import {useLanguage} from "../../../services/LanguageService.ts";import {clipboardItems, loadMoreItems, scrollState, searchBoxState} from "../context/ClipboardDataContext.ts";// 获取语言上下文const {currentLanguage} = useLanguage();</script><style scoped>.loading-indicator {  display: flex;  justify-content: center;  align-items: center;  height: 80px;}.no-more-indicator {  display: flex;  justify-content: center;  align-items: center;  height: 50px;}.empty {  display: flex;  justify-content: center;  align-items: center;  height: calc(100vh - 65px);}.clipboard-container {  height: calc(100vh - 65px);  /* 减去TitleBar(25px)和NavBar(30px)的高度 */}.clipboard-container-search {  height: calc(100vh - 110px) !important;  /* 减去TitleBar(25px)和NavBar(30px)的高度 */}.clipboard-container {  transition: height 0.3s ease;  z-index: 1;  position: relative;  overflow-y: auto;}/* 确保 transition-group 定位正常 */ul {  position: relative;  padding: 0;}</style>