<script setup lang="ts">import {clearAllData, scrollState, showClearDataModal} from "../composables/ClipboardDataComposable.ts";import ClearIcon from "../../../assets/icons/ClearIcon.vue";import {useLanguage} from "../../../services/LanguageService.ts";import {useMessage} from "naive-ui";const {currentLanguage} = useLanguage();const message = useMessage();/** * 点击清理数据按钮 */async function onClickClearData() {  await clearAllData(message, currentLanguage.value);}</script><template>  <div class="footer">    <div class="footer-left">      <!-- 清理数据 -->      <clear-icon class="clear-icon" @click="showClearDataModal = true"/>    </div>    <div class="footer-right">      <!-- 数据统计 -->      <div class="stats-text">        {{ currentLanguage.pages.list.totalData.replace('{total}', scrollState.total.toString()) }}      </div>    </div>  </div>  <n-modal v-model:show="showClearDataModal" :title="currentLanguage.pages.list.clearDataModalTitle"           preset="dialog">    <p>{{ currentLanguage.pages.list.clearDataModalContent }}</p>    <template #action>      <n-button @click="showClearDataModal = false">        {{ currentLanguage.pages.list.clearDataModalCancelBtn }}      </n-button>      <n-button type="primary" @click="onClickClearData">        {{ currentLanguage.pages.list.clearDataModalConfirmBtn }}      </n-button>    </template>  </n-modal></template><style scoped>.footer {  z-index: 999;  width: 96%;  height: 16px;  position: fixed;  bottom: 0;  font-size: 11px;  padding: 4px 7px;  color: var(--theme-universal-textHint);  background-color: var(--theme-universal-background);  display: flex;  justify-content: space-between;  border-top: 1px solid var(--theme-universal-border);  border-radius: 6px;  box-shadow: 0 -1px 5px var(--theme-universal-border);}.clear-icon {  width: 15px;  height: 15px;  opacity: 0.5;}.clear-icon:hover {  opacity: 0.8;}.clear-icon:focus-visible {  border: 0;}</style>