<template>  <div      class="clipboard-container"      key="clipboard-container"      :class="{'clipboard-container-search': searchBoxState.visible}"  >    <n-infinite-scroll        v-if="clipboardItems && clipboardItems.length > 0"        :distance="9"        @load="loadMoreItems"    >      <transition-group name="item" :css="animationEffect.enabled">        <ClipboardItem            v-for="(item, index) in clipboardItems"            :key="item.id"            :item="item"            :index="index"        />      </transition-group>      <!-- 最底部内容展示 -->      <div v-if="scrollState.isLoading" class="loading-indicator">        <n-spin :description="currentLanguage.pages.list.dataLoading"/>      </div>      <div v-if="!scrollState.hasMore" class="no-more-indicator">        {{ currentLanguage.pages.list.allLoaded }}      </div>    </n-infinite-scroll>    <!-- 无数据展示 -->    <n-empty v-else :description="currentLanguage.pages.list.empty" class="empty"/>  </div></template><script setup lang="ts">import {NEmpty, NInfiniteScroll, NSpin} from 'naive-ui';import ClipboardItem from './ClipboardItem.vue';import {useLanguage} from "../../../services/LanguageService.ts";import {clipboardItems, loadMoreItems, scrollState, searchBoxState} from "../composables/ClipboardDataComposable.ts";import {animationEffect} from "../composables/AnimationComposable.ts";// 获取语言上下文const {currentLanguage} = useLanguage();</script><style scoped>.loading-indicator {  display: flex;  justify-content: center;  align-items: center;  height: 80px;}.no-more-indicator {  display: flex;  justify-content: center;  align-items: center;  height: 50px;}.empty {  display: flex;  justify-content: center;  align-items: center;  height: calc(100vh - 80px);}.clipboard-container {  height: calc(100vh - 80px);  /* 减去TitleBar(25px)和NavBar(30px)的高度 */  z-index: 1;  position: relative;  overflow-y: auto;  will-change: height;  transition: height var(--animation-duration, 0.3s) ease, transform var(--animation-duration, 0.3s) ease;}.clipboard-container.clipboard-container-search {  height: calc(100vh - 155px);  transform: translateY(75px);  /* 减去TitleBar(25px)和NavBar(30px)的高度 */}/** * 代码块行号样式 */:deep(.n-code__line-numbers) {  color: var(--theme-universal-textHint);}/*内容列表动画效果start*//* 进入动画 - 从右侧进入 */.item-enter-from {  opacity: 0;  transform: translateX(100%);}.item-enter-to {  opacity: 1;  transform: translateX(0);}/* 离开动画 - 向左侧离开 */.item-leave-from {  opacity: 1;  transform: translateX(0);}.item-leave-to {  opacity: 0;  transform: translateX(-100%);}/* 离开中 */.item-leave-active {  position: absolute;  width: calc(100% - 12px); /* 考虑左右 margin */}/*内容列表动画效果end*/</style>