<script setup lang="ts">import {NImage} from "naive-ui";import {filePathConvertFileName, isText} from "../../../../utils/TextUtil.ts";import {convertFileSrc} from "@tauri-apps/api/core";import PptIcon from "./icons/PptIcon.vue";import UnknownFileIcon from "./icons/UnknownFileIcon.vue";import FileDeleteIcon from "./icons/FileDeleteIcon.vue";import ExeIcon from "./icons/ExeIcon.vue";import {isProgram} from "../../../../utils/ProgramUtil.ts";import {isVideo} from "../../../../utils/VideoUtil.ts";import {isImage} from "../../../../utils/ImageUtil.ts";import {isCode} from "../../../../utils/CodeUtil.ts";import {isAudio} from "../../../../utils/AudioUtil.ts";import {isExcel, isPDF, isPPT, isWord} from "../../../../utils/OfficeUtil.ts";import {isPackage} from "../../../../utils/PackageUtil.ts";import {displayThumbnailImage} from "../../composables/FileDataComposable.ts";const props = defineProps<{  filePath: string;  isFolder?: boolean;  isExist?: boolean;}>();</script><template>  <div class="file-item">    <file-delete-icon class="file-not-exist-icon" v-if="!isExist"/>    <font-awesome-icon icon="fa-regular fa-folder-closed" class="file-icon" v-else-if="isFolder"/>    <n-image        v-else-if="isImage(props.filePath) && displayThumbnailImage"        :src="convertFileSrc(props.filePath)"        object-fit="cover"        height="60px"        width="100%"        :lazy="true"        :preview-disabled="true"    />    <font-awesome-icon icon="fa-regular fa-image" class="file-icon"                       v-else-if="isImage(props.filePath) && !displayThumbnailImage"/>    <font-awesome-icon icon="fa-regular fa-file-video" class="file-icon" v-else-if="isVideo(props.filePath)"/>    <font-awesome-icon icon="fa-regular fa-file-audio" class="file-icon" v-else-if="isAudio(props.filePath)"/>    <font-awesome-icon icon="fa-regular fa-file-word" class="file-icon" v-else-if="isWord(props.filePath)"/>    <font-awesome-icon icon="fa-solid fa-table" class="file-icon" v-else-if="isExcel(props.filePath)"/>    <ppt-icon class="file-icon" v-else-if="isPPT(props.filePath)"/>    <font-awesome-icon icon="fa-regular fa-file-pdf" class="file-icon" v-else-if="isPDF(props.filePath)"/>    <font-awesome-icon icon="fa-regular fa-file-lines" class="file-icon" v-else-if="isText(props.filePath)"/>    <exe-icon class="file-icon" v-else-if="isProgram(props.filePath)"/>    <font-awesome-icon icon="fa-regular fa-file-zipper" class="file-icon" v-else-if="isPackage(props.filePath)"/>    <font-awesome-icon icon="fa-regular fa-file-code" class="file-icon" v-else-if="isCode(props.filePath)"/>    <unknown-file-icon class="file-icon" v-else/>    <span :class="{'file-not-exist-text':!props.isExist}">      {{ filePathConvertFileName(filePath) }}    </span>  </div></template><style scoped>.file-item {  margin: 2px;  padding: 3px;  border: 1px solid var(--theme-universal-border);  border-radius: 5px;  font-size: 12px;  width: 74px;  display: grid;  place-items: center;  grid-template-rows: 1fr auto;  position: relative;}.file-not-exist-icon {  width: 60px;  height: 60px;}.file-icon {  width: 50px;  height: 50px;}.file-item span {  width: 75px;  display: -webkit-box;  -webkit-box-orient: vertical;  -webkit-line-clamp: 1;  /* 显示一行 */  overflow: hidden;  text-align: center;  align-self: end;  justify-self: center;}.file-not-exist-text {  color: var(--theme-universal-textDelete);  text-decoration: line-through;}.preview-container {  position: absolute;  width: 80px;  height: 80px;  background-color: rgba(0, 0, 0, 0.6);  display: none;  border-radius: 5px;}.preview-container.show {  display: flex;  align-items: center;  justify-content: center;}.preview-icon {  color: rgb(214, 214, 214);  width: 50%;  height: 50%;  opacity: 0.6;}.preview-icon:hover {  opacity: 1;}</style>