<script setup lang="ts">import {h, onMounted, reactive, ref, watch} from 'vue';import {convertFileSize, getFileSize} from "../../../utils/FileUtil.ts";import {convertChildren, getType, readZipData} from "../../../utils/PackageUtil.ts";import FileToBigIcon from "../../../assets/icons/FileToBigIcon.vue";import {NGradientText, useMessage} from "naive-ui";import {readFile} from "@tauri-apps/plugin-fs";import {filePathConvertFileName} from "../../../utils/strUtil.ts";import {PackageTreeOption} from "../../../types/PackageTreeOption";import {Uint8ArrayReader, ZipReader} from "@zip.js/zip.js";import {updatePrefixWithExpanded} from "../../../utils/TreeUtil.ts";import CloseFolderIcon from "../../../assets/icons/CloseFolderIcon.vue";import {invoke} from "@tauri-apps/api/core";import NonsupportPreviewIcon from "../../../assets/icons/NonsupportPreviewIcon.vue";const message = useMessage();const props = defineProps<{  filePath: string}>()// 文件信息const fileData = reactive({  canPreview: false,              // 是否可预览  size: '0B',                     // 文件大小  type: '',                       // 文件类型  password: '',                   // 密码  isEncrypt: false,               // 是否加密  isLoadingFile: true,            // 是否读取文件  content: new Uint8Array(0),     // 文件数据});const folderTree = ref<PackageTreeOption[]>();async function loadFileInfo() {  fileData.isLoadingFile = true;  const size = await getFileSize(props.filePath);  fileData.size = convertFileSize(size);  fileData.canPreview = size < 1024 * 1024 * 1024;  fileData.type = getType(props.filePath);  if (fileData.canPreview) {    // 解压文件内容    await onUncompressing();  }  fileData.isLoadingFile = false;}/** * 文件解压缩 */async function onUncompressing() {  // 解压 ZIP  if (fileData.type === 'application/zip' && await onUncompressZIP()) {    return;  }  // 解压 RAR  if (fileData.type === 'application/x-rar-compressed' && await onUncompressRAR()) {    return;  }  // 解压 TAR  if (fileData.type === 'application/x-tar' && await onUncompressTAR()) {    return;  }  // 解压 GZIP  if (fileData.type === 'application/gzip' && await onUncompressGZIP()) {    return;  }  // todo 解压 7ZIP  if (fileData.type === 'application/x-7z-compressed') {    return;  }}/** * 解析 ZIP */async function onUncompressZIP() {  fileData.content = await readFile(props.filePath);  let content;  // 创建 ZipReader 实例  const reader = new ZipReader(new Uint8ArrayReader(fileData.content));  try {    // 获取 ZIP 中的所有条目（文件）    const entries = await reader.getEntries();    console.log('ZIP 文件条目：', entries)    // 读取zip文件内容    content = await readZipData(entries);  } catch (err: any) {    console.error('解压失败:', err);    message.error("文件解压缩失败，错误原因：" + err.message);    return false;  } finally {    // 关闭 reader    await reader.close();  }  // 文件夹内容转换成文件夹树  const children = convertChildren(content);  // 填充文件夹树  fillFolderTree(children);  return true;}/** * 解析 RAR */async function onUncompressRAR() {  console.log("开始解析 RAR")  const content = JSON.parse(await invoke('read_rar_data', {path: props.filePath}));  console.log("RAR 文件内容：", content)  // 文件夹内容转换成文件夹树  const children = convertChildren(content);  // 填充文件夹树  fillFolderTree(children);  return true;}/** * 解析 TAR */async function onUncompressTAR() {  console.log("开始解析 TAR")  const content = JSON.parse(await invoke('read_tar_data', {path: props.filePath}));  console.log("TAR 文件内容：", content)  // 文件夹内容转换成文件夹树  const children = convertChildren(content);  // 填充文件夹树  fillFolderTree(children);  return true;}/** * 解析 GZIP */async function onUncompressGZIP() {  console.log("开始解析 GZIP")  const content = JSON.parse(await invoke('read_gzip_data', {path: props.filePath}));  console.log("TAR 文件内容：", content)  // 文件夹内容转换成文件夹树  const children = convertChildren(content);  // 填充文件夹树  fillFolderTree(children);  return true;}/** * 填充文件夹树 * @param children 文件夹内容 */function fillFolderTree(children: PackageTreeOption[]) {  const baseFileName = filePathConvertFileName(props.filePath);  folderTree.value = [{    key: baseFileName,    label: baseFileName,    children,    prefix: () => h(CloseFolderIcon, null, {default: () => h(CloseFolderIcon)}),    suffix: () => h(NGradientText, {type: 'success'}, {      default: () => {        return children.map(node => (node.fileCount || 1) + 1).reduce((acc, count) => acc + count, 0) + "个文件"      }    })  }];}watch(() => props.filePath, loadFileInfo)onMounted(loadFileInfo)</script><template>  <div v-if="fileData.isLoadingFile" class="no-preview-container">    <n-spin/>  </div>  <div v-else-if="!fileData.canPreview" class="no-preview-container">    <file-to-big-icon class="no-preview-icon"/>    <span class="no-preview-text">      文件大小超过 1G，暂不支持预览，请点击右上角打开文件所在位置进行浏览    </span>  </div>  <div v-if="fileData.canPreview && fileData.type ==='application/x-7z-compressed'" class="no-preview-container">    <nonsupport-preview-icon class="no-preview-icon"/>    <span class="no-preview-text">暂不支持预览7z格式的文件</span>  </div>  <div v-else class="folder-container">    <n-tree        :data="folderTree"        :on-update:expanded-keys="updatePrefixWithExpanded"        expand-on-click        show-line        cascade        block-line    />  </div>  <div class="file-info">    <div>文件大小：{{ fileData.size }}</div>    <div>文件类型：{{ fileData.type }}</div>  </div></template><style scoped>.no-preview-container {  width: 100%;  height: 95%;  border: 0;  display: flex;  flex-direction: column;  justify-content: center;  align-items: center;}.no-preview-icon {  width: 40%;  height: auto;  opacity: 0.8;}.no-preview-text {  width: 70%;  text-align: center;  font-size: 16px;  opacity: 0.8;}.folder-container {  width: 100%;  height: 95%;  overflow-y: auto;}.file-info {  display: flex;  justify-content: space-between;}</style>