<script setup lang="ts">import TitleBar from "../../components/TitleBar.vue";import TagList from "./components/TagList.vue";import TagForm from "./components/TagForm.vue";import {useLanguage} from "../../services/LanguageService.ts";import {destroyAnimationEffectContext, initializeAnimationEffectContext} from "./composables/AnimationComposable.ts";import {onMounted, onUnmounted} from "vue";const {currentLanguage} = useLanguage();/** * 组件初始化挂载监听 */onMounted(async () => {  // 初始化动画效果上下文  await initializeAnimationEffectContext();})/** * 组件卸载时清除事件监听 */onUnmounted(async () => {  // 销毁动画效果上下文  destroyAnimationEffectContext();})</script><template>  <div class="tag-manager-container">    <TitleBar :title="currentLanguage.pages.tags.title"              :showCloseBtn="true"              :dev-tool="`tags`"/>    <div class="tag-manager-content">      <!-- 左右分栏布局 -->      <div class="tag-manager-layout">        <!-- 左侧标签列表 -->        <TagList/>        <!-- 右侧详情/编辑面板 -->        <TagForm/>      </div>    </div>  </div></template><style scoped>.tag-manager-container {  height: 100vh;  display: flex;  flex-direction: column;  background-color: var(--theme-universal-background);  color: var(--theme-universal-text);}.tag-manager-content {  flex: 1;  overflow: hidden;  position: relative;}/* 左右分栏布局 */.tag-manager-layout {  display: flex;  height: 100%;  position: relative;  overflow: hidden;  /* 确保超出部分被隐藏 */}</style>