/** * 商店插件 */declare interface StorePlugin {    id: string;             // 插件唯一id    name: string;           // 插件名称    version: string;        // 插件版本    description: string;    // 插件描述    platform: string;       // 可用平台，Windows，Mac，General    downloadUrl: string;    // 插件下载地址    size: number;           // 插件安装包大小，单位：字节}/** * 本地插件 */declare interface LocalPlugin {    id: number;             // id    plugin_id: string;      // 插件唯一id    plugin_name: string;    // 插件名称    version: string;        // 插件版本    use_location: string;   // 可用位置，list，settings，tags，preview    platform: string;       // 可用平台，Windows，Mac，General    url: string;            // 插件下载地址    enable: number;         // 插件是否启用    description: string;    // 插件描述    install_time: number;   // 安装时间}/** * 选择的插件 */declare interface SelectPlugin {    id: number | null;      // id    plugin_id: string;      // 插件唯一id    plugin_name: string;    // 插件名称    version: string;        // 插件版本    platform: string;       // 可用平台，Windows，Mac，General    url: string;            // 插件下载地址    enable: number | null;  // 插件是否启用    description: string;    // 插件描述}